
public class Ex02_자료형 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 1. 논리형 데이터 타입 boolean
		boolean check = true;
		System.out.println(check);
		
		// 2. 문자형 데이터 타입 char
		char ch = '4';
		System.out.println(ch);
		
		// 3. 정수형 데이터 타입
		// byte, short, int, long
		// 1byte, 2byte, 4byte, 8byte
		int salary = 10000000;
		System.out.println(salary);
		
		//4. 실수형 데이터 타입
		// float, double
		// 4byte, 8byte
		float day = (float)12.18;
		// float day = 12.18f;
		
		
		double a = 1.1;
		double b = 2.2;
		
		System.out.println(a+b);
		
		
	}

}
